import React, { useContext, useEffect, useState } from 'react';
import '../styles/LandingPage.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';
const currentYear = new Date().getFullYear();

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);

  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
  }, []);
  
  const [Flights, setFlights] = useState([]);
  const { setTicketBookingDate } = useContext(GeneralContext);
  const userId = localStorage.getItem('userId');
  const fetchFlights = async () => {
    const today = new Date();

    if (checkBox) {
      const start = new Date(departureDate);
      const end = new Date(returnDate);

      if (departure && destination && departureDate && returnDate) {
        if (start > today && end > start) {
          setError('');
          const res = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(res.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    } else {
      const start = new Date(departureDate);
      if (departure && destination && departureDate) {
        if (start >= today) {
          setError('');
          const res = await axios.get('http://localhost:6001/fetch-flights');
          setFlights(res.data);
        } else {
          setError('Please check the dates');
        }
      } else {
        setError('Please fill all the inputs');
      }
    }
  };

  const handleTicketBooking = (id, origin, destination) => {
    if (userId) {
      if (origin === departure) {
        setTicketBookingDate(departureDate);
        navigate(`/book-flight/${id}`);
      } else if (destination === departure) {
        setTicketBookingDate(returnDate);
        navigate(`/book-flight/${id}`);
      }
    } else {
      navigate('/auth');
    }
  };

  return (
    <div className="landingPage">
      <div className="landingHero">
        <div className="landingHero-title">
  <h1 className="banner-h1">Book Your Next Flight with Ease and Confidence</h1>
  <p className="banner-p">
    Discover seamless travel planning, curated flight options, and a hassle-free booking experience — all tailored to get you where you want to go, effortlessly.
  </p>
</div>


        <div className="Flight-search-container input-container mb-4">
          <div className="form-check form-switch">
            <input className="form-check-input" type="checkbox" id="flexSwitchCheckDefault" onChange={(e) => setCheckBox(e.target.checked)} />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Return journey</label>
          </div>

          <div className="Flight-search-container-body">
            <div className="form-floating">
              <select className="form-select form-select-sm mb-3" value={departure} onChange={(e) => setDeparture(e.target.value)}>
                <option value="" disabled>Select</option>
                <option value="Chennai">Chennai</option>
                <option value="Banglore">Banglore</option>
                <option value="Hyderabad">Hyderabad</option>
                <option value="Mumbai">Mumbai</option>
                <option value="Indore">Indore</option>
                <option value="Delhi">Delhi</option>
                <option value="Pune">Pune</option>
                <option value="Trivendrum">Trivendrum</option>
                <option value="Bhopal">Bhopal</option>
                <option value="Kolkata">Kolkata</option>
                <option value="varanasi">varanasi</option>
                <option value="Jaipur">Jaipur</option>
              </select>
              <label htmlFor="floatingSelect">Departure City</label>
            </div>

            <div className="form-floating">
              <select className="form-select form-select-sm mb-3" value={destination} onChange={(e) => setDestination(e.target.value)}>
                <option value="" disabled>Select</option>
                <option value="Chennai">Chennai</option>
                <option value="Banglore">Banglore</option>
                <option value="Hyderabad">Hyderabad</option>
                <option value="Mumbai">Mumbai</option>
                <option value="Indore">Indore</option>
                <option value="Delhi">Delhi</option>
                <option value="Pune">Pune</option>
                <option value="Trivendrum">Trivendrum</option>
                <option value="Bhopal">Bhopal</option>
                <option value="Kolkata">Kolkata</option>
                <option value="varanasi">varanasi</option>
                <option value="Jaipur">Jaipur</option>
              </select>
              <label htmlFor="floatingSelect">Destination City</label>
            </div>

            <div className="form-floating mb-3">
              <input type="date" className="form-control" id="floatingInputstartDate" value={departureDate} onChange={(e) => setDepartureDate(e.target.value)} />
              <label htmlFor="floatingInputstartDate">Journey date</label>
            </div>

            {checkBox && (
              <div className="form-floating mb-3">
                <input type="date" className="form-control" id="floatingInputreturnDate" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
                <label htmlFor="floatingInputreturnDate">Return date</label>
              </div>
            )}

            <div>
              <button className="btn btn-primary" onClick={fetchFlights}>Search</button>
            </div>
          </div>

          <p>{error}</p>
        </div>

        {Flights.length > 0 && (
          Flights.filter(f => f.origin === departure && f.destination === destination).length > 0 ? (
            <div className="availableFlightsContainer">
              <h1>Available Flights</h1>
              <div className="Flights">
                {Flights
                  .filter(f => checkBox
                    ? (f.origin === departure && f.destination === destination) || (f.origin === destination && f.destination === departure)
                    : f.origin === departure && f.destination === destination)
                  .map(flight => (
                    <div className="Flight" key={flight._id}>
                      <div>
                        <p><b>{flight.flightName}</b></p>
                        <p><b>Flight Number:</b> {flight.flightId}</p>
                      </div>
                      <div>
                        <p><b>Start :</b> {flight.origin}</p>
                        <p><b>Departure Time:</b> {flight.departureTime}</p>
                      </div>
                      <div>
                        <p><b>Destination :</b> {flight.destination}</p>
                        <p><b>Arrival Time:</b> {flight.arrivalTime}</p>
                      </div>
                      <div>
                        <p><b>Starting Price:</b> {flight.basePrice}</p>
                        <p><b>Available Seats:</b> {flight.totalSeats}</p>
                      </div>
                      <button className="btn btn-primary" onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}>Book Now</button>
                    </div>
                  ))}
              </div>
            </div>
          ) : (
            <div className="availableFlightsContainer">
              <h1>No Flights</h1>
            </div>
          )
        )}

      </div>

      <section id="about" className="section-about p-4">
        <div className="container">
          <h2 className="section-title">About Us</h2>
          <p className="section-description">
            &nbsp;&nbsp;&nbsp;&nbsp; Welcome to our Flight ticket booking app, where we are dedicated to providing you with an exceptional travel experience...
          </p>
          <p className="section-description">
            &nbsp;&nbsp;&nbsp;&nbsp; Our user-friendly interface allows you to effortlessly browse through flight schedules...
          </p>
          <p className="section-description">
            &nbsp;&nbsp;&nbsp;&nbsp; Start your journey today and let us be your trusted companion...
          </p>
          <span><h5>{currentYear} SkySwift - &copy; All rights reserved</h5></span>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
