import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { GeneralContext } from '../context/GeneralContext';
import '../styles/Navbar.css'; // ✅ make sure this path is correct

const Navbar = () => {
  const { currentUser, logout } = useContext(GeneralContext);
  const navigate = useNavigate();

  return (
    <div className="navbar">
      <div className="navbar-left" onClick={() => navigate('/')}>
        <span className="brand">FlightFinder</span>
      </div>

      <div className="navbar-center">
        <Link to="/">Home</Link>
        <Link to="/flights">Flights</Link>
        {currentUser?.usertype === 'admin' && <Link to="/admin">Admin</Link>}
        {currentUser?.usertype === 'flight-operator' && <Link to="/flight-admin">Operator</Link>}
      </div>

      <div className="navbar-right">
        {!currentUser ? (
          <>
            <button onClick={() => navigate('/login')}>Login</button>
            <button onClick={() => navigate('/register')}>Register</button>
          </>
        ) : (
          <>
            <span style={{ fontWeight: 'bold', color: '#fff' }}>
              {currentUser.username}
            </span>
            <button onClick={logout}>Logout</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Navbar;
